    public int maxOperations(int[] nums) {
        record = new int[nums.length][nums.length];

        int num1 = nums[0] + nums[1];
        int num2 = nums[0] + nums[nums.length - 1];
        int num3 = nums[nums.length - 1] + nums[nums.length - 2];

        for (int i = 0; i < record.length; i++) {
            for (int j = 0; j < record[i].length; j++) {
                record[i][j] = -1;
            }
        }
        int count1 = recOperate(num1, nums, 0, nums.length - 1);
        // System.out.println("结束一轮");
        for (int i = 0; i < record.length; i++) {
            for (int j = 0; j < record[i].length; j++) {
                record[i][j] = -1;
            }
        }
        int count2 = recOperate(num2, nums, 0, nums.length - 1);
        // System.out.println("结束二轮");
        for (int i = 0; i < record.length; i++) {
            for (int j = 0; j < record[i].length; j++) {
                record[i][j] = -1;
            }
        }
        int count3 = recOperate(num3, nums, 0, nums.length - 1);
        // System.out.println("结束三轮");

        return Math.max(Math.max(count1, count2), count3);

    }

    public int recOperate(int num, int[] nums, int left, int right) {
        if (left >= right) {
            return 0;
        }

        if (record[left][right] != -1) {
            return record[left][right];
        }


        int flag1 = 0, flag2 = 0, flag3 = 0;
        if (nums[left] + nums[left + 1] == num) {
            flag1 = recOperate(num, nums, left + 2, right) + 1;
        }
        if (nums[left] + nums[right] == num) {
            flag2 = recOperate(num, nums, left + 1, right - 1) + 1;
        }
        if (nums[right] + nums[right - 1] == num) {
            flag3 = recOperate(num, nums, left, right - 2) + 1;
        }

        int max = Math.max(Math.max(flag1, flag2), flag3);

        record[left][right] = max;

        // return Math.max(Math.max(flag1, flag2), flag3);
        return max;
    }

